package day03;

public class C06_PreIncrementPostIncrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int sayi=10;
		sayi++;
		sayi++;
		sayi++;
		System.out.println(sayi);
		System.out.println(sayi++);
		System.out.println(sayi);
		
		System.out.println(++sayi);
		

	}

}
