package day06_wrepper;

public class C03_if {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=300;
		int b=20;
		
		if (a>5) {
			System.out.println("a sayisi besden büyük");
			
		}
		if (a>b) {
			System.out.println("a sayisi b den büyük");
			
		}
		
		if (b>100) {
			System.out.println("b sayisi 100 den büyük");
		}
		
		
		//basit if cumleleri bagimsiz calisir her bir if cumlesi 
		// sartina bakar sart true ise yazdirir false ise yazdirmaz
		
		

	}

}
