package day45collection;

import java.util.Deque;
import java.util.LinkedList;
import java.util.List;
import java.util.Queue;

public class C02_LInkedlist {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		List<Integer> ll=new LinkedList<>();
		ll.add(5);
		ll.add(7);
		
		// list parent oldugu icin list intherit olmasina ragmen overrid
		// list methodlarini kullanambildik
		
		Queue<Integer> ll1=new LinkedList<>();
		ll1.add(5);
		ll1.add(7);
	
		// data type Queue secilince queue den gelen methodlari kullanabiliriz
	
		Deque<Integer> ll2=new LinkedList<>();
		ll2.add(5);
		ll2.add(7);
	
	
	}

}
